    public static boolean ifSurroundedWithZeros(int row, int column, int[][] matrix) {

        int maxRow = matrix.length - 1;
        int maxColumn = matrix[0].length - 1;

        if (
                row - 1 >= 0 &&
                        row + 1 <= maxRow &&
                        column - 1 >= 0 &&
                        column + 1 <= maxColumn
        ) {

            return matrix[row - 1][column - 1] == 0 &&
                    matrix[row - 1][column] == 0 &&
                    matrix[row - 1][column + 1] == 0 &&

                    matrix[row][column - 1] == 0 &&
                    matrix[row][column + 1] == 0 &&

                    matrix[row + 1][column - 1] == 0 &&
                    matrix[row + 1][column] == 0 &&
                    matrix[row + 1][column + 1] == 0;

        } else {
            return false;
        }
    }


    public static int[][] updateMatrix1(int[][] matrix) {
        int stepsFromLastZero;
        boolean isFilled = false;
        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[i].length; j++) {
                if (matrix[i][j] == 0) {
                    for (int k = 0; k < matrix.length; k++) {
                        for (int m = 0; m < matrix[k].length; m++) {
                            stepsFromLastZero = Math.abs(i - k) + Math.abs(j - m);
                            if (!isFilled && matrix[k][m] != 0)
                                matrix[k][m] = stepsFromLastZero;
                            if (isFilled && matrix[k][m] != 0 && stepsFromLastZero < matrix[k][m])
                                matrix[k][m] = stepsFromLastZero;
                        }
                    }
                    isFilled = true;
                }
            }
        }
        return matrix;
    }
